import { connect } from "frontity";
import React from "react";
import MainHeader from "./header";
import Navigation from "./navigation";
import SocialNav from "./social-menu";
import SiteMenuIcons from "./header-icons";

const Header = ({ state, actions }) => { 

    const { items } = state.source.get("/wp-api-menus/v2/menus/3");
    const Items = items.items;
    var listMenus = [];
    // console.log(Items);
    for (let index = 0; index < Items.length; index++) {
      const name = Items[index].title;
      const link = "/"+ Items[index].object_slug + "/";
      var list = [name, link];
      listMenus.push(list);
  
    }   
    return(
    <MainHeader >
        <Navigation menu = { listMenus }/> 
        {/* {
            state.theme.showSocialLinks && ( <SocialNav menu = { state.theme.socialLinks }/>
            )
        }  */}
        <SiteMenuIcons />
    </MainHeader>);
};

export default connect(Header);