import { Box, CSSReset, theme, ThemeProvider } from "@chakra-ui/core";
import { connect, Head } from "frontity";
import React from "react";
import Archive from "./archive";
import Footer from "./footer";
import Header from "./header";
import Loading from "./loading";
import Page404 from "./page404";
import Post from "./post/post";
import SearchResults from "./search";
import Title from "./title";
import FontFace from "./styles/font-face";
import Switch from "@frontity/components/switch";
import SliderSection from "./slider/slider.js";
import ServicesBoxSection from "./services/services.js";
import WorkBoxSection from "./work/work.js";
import WhoBoxSection from "./who/who.js";
import Nav from "./fetchmenu/menu.js";


const data = ({ state}) => state.source.get(state.router.link);
console.log(data);
// Theme is the root React component of our theme. The one we will export
// in roots.
const Theme = ({ state, libraries }) => {
  // Get information about the current URL.
  const data = state.source.get(state.router.link);
  const parse = libraries.source.parse(state.router.link);
  // Check if the url is a search type
  const isSearch = Boolean(parse.query["s"]);

  return (
    <ThemeProvider
      theme={{
        ...theme,
        fonts: {
          ...theme.fonts,
          heading: "Montserrat, sans-serif"
        },
        colors: { ...theme.colors, ...state.theme.colors }
      }}
    >
      <FontFace />
      <CSSReset />

      <Title />
      <Head>
        <meta name="description" content={state.frontity.description} />
        <html lang="en" />
      </Head>

      <Header />
      <Box
        as="main"
        mt={{ base: "40px", md: "70px" }}
        minH="calc(100vh - 320px)"
      >
        <Switch>
            <Loading when={data.isFetching} />
            <Post when={data.isPostType} />
            <Page404 when={data.is404}/>
        </Switch>          
          <Switch>
            <SliderSection when={data.isHome} />
          </Switch>
          <Switch>
            <ServicesBoxSection when={data.isHome} />
          </Switch>
          <Switch>
            <WorkBoxSection when={data.isHome} />
          </Switch>
          <Switch>
            <WhoBoxSection when={data.isHome} />
          </Switch>
          <Nav />
      </Box>
      <Footer />
    </ThemeProvider>
  );
};

export default connect(Theme);
