import { Box, PseudoBox, SimpleGrid } from "@chakra-ui/core";
import React from "react";
import { connect } from "frontity";

const ServicesBox = props => (
  <Box
    pos="relative"
    bg="#3d3b3b"
    py={{ base: "32px", lg: "40px" }}
    {...props}
  />
);

const ServiceSectionGroup = props => (
  <SimpleGrid
    columns={{ base: 1, md: 1 }}
    maxWidth="1150px"
    mx="auto"
    p = {6}
    width="90%"
    {...props}
  />
);

const property = {
    h1: "“From social media agencyto Super Bowl powerhouse”-AdWeek"
}
const ServicesContainer = props => (
    <Box 
        color="#fff"
        as="h1"
        width="85%"
        textTransform="uppercase"
        alignItems="center"
        textAlign="center"
        fontSize="2.5em"
        {...props}

    />
);

const ServicesBoxSection1 = ({ state }) => (
  <ServicesBox alignSelf="flex-end">
    <ServiceSectionGroup>
        <ServicesContainer>
            {property.h1}
        </ServicesContainer>

    </ServiceSectionGroup>
  </ServicesBox>
);

export default connect(ServicesBoxSection1);
